mapper.select:
SELECT COUNT(QBOARD_NO) FROM QUESTION_BOARD WHERE 1 = 1
AND (USER_ID LIKE '%'|| #{searchKeyword} ||'%' OR BOARD_TITLE LIKE '%'|| #{searchKeyword} ||'%' OR BOARD_CONTENT LIKE '%'|| #{searchKeyword} ||'%')
AND BOARD_ADOPT = #{boardAdopt}

SELECT * FROM (SELECT ROWNUM R_NUM, B.* from (SELECT * FROM QUESTION_BOARD WHERE 1 = 1
AND ( USER_ID LIKE '%'|| #{searchKeyword} ||'%' OR BOARD_TITLE LIKE '%'|| #{searchKeyword} ||'%' OR BOARD_CONTENT LIKE '%'|| #{searchKeyword} ||'%')
AND BOARD_ADOPT = #{boardAdopt}
ORDER BY ${ordering} DESC) B) WHERE R_NUM BETWEEN #{begin} AND #{end}

update.if:
UPDATE QUESTION_BOARD
 SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, BOARD_FILE = #{boardFile},
  BOARD_ADOPT = #{boardAdopt}, BOARD_HIT = #{boardHit}, BOARD_LIKE = #{boardLike}
 WHERE QBOARD_NO = #{qboardNo}

mapper.select:
SELECT * FROM QNA_LIKE
WHERE USER_ID = #{userId}
AND BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM QNA_LIKE
WHERE USER_ID = #{userId}
AND BOARD_NO = #{boardNo}

mapper.select:
SELECT * FROM BOARD_REPORT
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NO = #{boardNo}
AND REPORTER = #{userId}
