mapper.update:
UPDATE CALL_CENTER
 SET QNA_TITLE = #{qnaTitle}, QNA_CONTENT = #{qnaContent}
 , QNA_FILE = #{qnaFile},
  QNA_ADOPT = #{qnaAdopt}
 WHERE QNA_NO = #{qnaNo}

mapper.select:
SELECT * FROM (SELECT ROWNUM R_NUM, B.* from (SELECT * FROM CALL_CENTER WHERE 1 = 1 AND USER_ID = #{userId}
AND ( QNA_TITLE LIKE '%'|| #{searchKeyword} ||'%' OR QNA_CONTENT LIKE '%'|| #{searchKeyword} ||'%')
ORDER BY QNA_REGDATE DESC) B) WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT * FROM CALL_CENTER WHERE 1 = 1 AND USER_ID = #{userId}
OR QNA_TITLE LIKE '%'|| #{searchKeyword} ||'%' OR QNA_CONTENT LIKE '%'|| #{searchKeyword} ||'%'
ORDER BY QNA_REGDATE DESC
