mapper.select:
SELECT * FROM
(SELECT ROWNUM R_NUM,
K.* FROM (SELECT * FROM ROADMAP WHERE USER_ID = #{userId} ORDER BY RBOARD_NO) K
) WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT * FROM (SELECT ROWNUM AS ROW_NUM, C.* FROM (SELECT * FROM ROADMAP
AND CATEGORY_NAME = #{categoryName}
ORDER BY RBOARD_NO DESC) C) R WHERE ROW_NUM BETWEEN #{begin} AND #{end}

SELECT R.* FROM (SELECT ROWNUM AS ROW_NUM, C.* FROM (SELECT * FROM ROADMAP
NOT USER_ID IN (SELECT USER_ID FROM USERS WHERE GRADE = '탈퇴')
AND CATEGORY_NAME = #{categoryName}
AND RBOARD_TITLE LIKE '%'||#{searchKeyword}||'%'
AND RBOARD_CONTENT LIKE '%'||#{searchKeyword}||'%'
AND USER_NAME LIKE '%'||#{searchKeyword}||'%'
USER_COUNT DESC RBOARD_NO DESC RBOARD_NO ASC
) C) R WHERE ROW_NUM BETWEEN #{begin} AND #{end}
